    }


		$c = clone $c;

		// Set the correct dbName if it has not been overridden
		if ($c->getDbName() == Propel::getDefaultDB()) {
			$c->setDbName(self::DATABASE_NAME);
		}

		SpyFormBuilderFieldsPeer::addSelectColumns($c);
		$startcol2 = (SpyFormBuilderFieldsPeer::NUM_COLUMNS - SpyFormBuilderFieldsPeer::NUM_LAZY_LOAD_COLUMNS);

		SpyFormBuilderPeer::addSelectColumns($c);
		$startcol3 = $startcol2 + (SpyFormBuilderPeer::NUM_COLUMNS - SpyFormBuilderPeer::NUM_LAZY_LOAD_COLUMNS);

		$c->addJoin(array(SpyFormBuilderFieldsPeer::FORM_ID,), array(SpyFormBuilderPeer::ID,), $join_behavior);
		$stmt = BasePeer::doSelect($c, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = SpyFormBuilderFieldsPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = SpyFormBuilderFieldsPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$omClass = SpyFormBuilderFieldsPeer::getOMClass();

				$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
				$obj1 = new $cls();
				$obj1->hydrate($row);
				SpyFormBuilderFieldsPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

			// Add objects for joined SpyFormBuilder rows

			$key2 = SpyFormBuilderPeer::getPrimaryKeyHashFromRow($row, $startcol2);
			if ($key2 !== null) {
				$obj2 = SpyFormBuilderPeer::getInstanceFromPool($key2);
				if (!$obj2) {

					$omClass = SpyFormBuilderPeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					SpyFormBuilderPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 loaded

				// Add the $obj1 (SpyFormBuilderFields) to the collection in $obj2 (SpyFormBuilder)
				$obj2->addSpyFormBuilderFields($obj1);
			} // if joined row not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


